import React, { useState } from 'react';
import './signUp.css';

const Signup = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        idNumber: '',
        accountNumber: '',
        username: '',
        password: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
        console.log(`Updated ${name}:`, value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/user/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.log(error.message);
        } finally {
            setFormData({
                firstName: '',
                lastName: '',
                idNumber: '',
                accountNumber: '',
                username: '',
                password: ''
            });
        }
    };

    return (
        <div className="center-form">
            <form className="signup-form" onSubmit={handleSubmit}>
                <h1>Sign Up</h1>
                <div className="form-group">
                    <label htmlFor="firstName">First Name</label>
                    <input type="text" id="firstName" name="firstName" placeholder="Enter first name" value={formData.firstName} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="lastName">Last Name</label>
                    <input type="text" id="lastName" name="lastName" placeholder="Enter last name" value={formData.lastName} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="idNumber">ID Number</label>
                    <input type="text" id="idNumber" name="idNumber" placeholder="Enter ID Number" value={formData.idNumber} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="accountNumber">Account Number</label>
                    <input type="text" id="accountNumber" name="accountNumber" placeholder="Enter account number" value={formData.accountNumber} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input type="text" id="username" name="username" placeholder="Enter username" value={formData.username} onChange={handleInputChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" id="password" name="password" placeholder="Enter password" value={formData.password} onChange={handleInputChange} required />
                </div>
                <button type="submit" className="submit-button">Sign Up</button>
            </form>
        </div>
    );
};

export default Signup;
