import DOMPurify from 'dompurify';

export const sanitizeInput = (input) => 
  DOMPurify.sanitize(input, {
    ALLOWED_TAGS: [],
    ALLOWED_ATTR: []
  });

export const sanitizeHTML = (dirty) => ({
  __html: DOMPurify.sanitize(dirty, {
    ALLOWED_TAGS: ['b', 'i', 'em', 'strong'],
    ALLOWED_ATTR: []
  })
});
