const API_BASE_URL = 'http://localhost:5000/api';

export const API_ENDPOINTS = {
    AUTH: {
        REGISTER: `${API_BASE_URL}/auth/register`,
        LOGIN: `${API_BASE_URL}/auth/login`,
        PROFILE: '/api/auth/profile',
    },
    USER: {
        PROFILE: `${API_BASE_URL}/user/profile`,
    },
    TRANSACTION: {
        CREATE: '/api/transaction/create',
        MY_TRANSACTIONS: '/api/transaction/my-transactions',
        GET_BY_ID: (id) => `${API_BASE_URL}/transaction/${id}`,
        ADMIN: {
            PENDING: '/api/transaction/admin/pending',
            VERIFY: '/api/transaction/admin/verify',
            VALIDATE_SWIFT: '/api/transaction/admin/validate-swift'
        }
    }
};
