import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/theme.css';
import './login.css';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    account_number: '',
    password: ''
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    console.log(`Input changed: ${e.target.name}`);
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    console.log('Updated form data:', { ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form submission started');
    console.log('Form data being sent:', { ...formData, password: '****' });

    try {
      console.log('Making API request to login endpoint');
      const response = await fetch('http://localhost:5000/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Response data:', { ...data, token: '****' });
      
      if (response.ok) {
        console.log('Login successful');
        localStorage.setItem('token', data.token);
        console.log('Token stored in localStorage');
        
        localStorage.setItem('user', JSON.stringify(data.user));
        console.log('User data stored in localStorage');
        
        console.log('Navigating to dashboard');
        navigate('/dashboard');
      } else {
        console.log('Login failed:', data.message);
        setError(data.message);
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Failed to connect to server');
    }
  };

  return (
    <div className="page-container">
      <div className="form-container">
        <form className="login-form" onSubmit={handleSubmit}>
          <h1>Login</h1>
          {error && <div className="error-message">{error}</div>}
          
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="account_number">Account Number</label>
            <input
              type="text"
              id="account_number"
              name="account_number"
              value={formData.account_number}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="submit-button">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
